import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TodoList.css';

const STATUS_OPTIONS = ['not started', 'todo', 'in progress', 'done'];

const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);
    const pad = (num) => num.toString().padStart(2, '0');
    const year = date.getFullYear();
    const month = pad(date.getMonth() + 1);
    const day = pad(date.getDate());
    const hours = pad(date.getHours());
    const minutes = pad(date.getMinutes());
    const seconds = pad(date.getSeconds());
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
};

const isOverdue = (dueTime) => {
    if (!dueTime) return false;
    return new Date(dueTime) < new Date();
};

const formatTime = (timestamp, prefix) => {
    if (!timestamp) return '';
    try {
        const date = new Date(timestamp);
        return `${prefix}: ${date.toLocaleDateString()} ${date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    } catch {
        return `${prefix}: Invalid Date`;
    }
};

const formatDueTimeForInput = (dueTime) => {
    if (!dueTime) return '';
    try {
        const [datePart, timePart] = dueTime.split(' ');
        return `${datePart}T${timePart.substring(0, 5)}`;
    } catch {
        return '';
    }
};

const TodoCard = ({ todo, deleteTodo, updateStatus, openEditModal }) => {
    const isDone = todo.status === 'done';
    const isTaskOverdue = isOverdue(todo.due_time) && !isDone;
    const completedClass = isDone ? 'line-through done-task-text' : '';
    const getStatusClass = (status) => {
        switch (status) {
            case 'done': return 'status-done';
            case 'in progress': return 'status-in-progress';
            case 'todo': return 'status-todo';
            case 'not started': return 'status-not-started';
            default: return '';
        }
    };

    return (
        <div className={`todo-card ${isTaskOverdue ? 'overdue' : ''}`}>
            <h3 className={completedClass}>{todo.title}</h3>
            <p>{todo.description}</p>
            <p>{formatTime(todo.create_time, 'Created')}</p>
            {todo.due_time && <p>{formatTime(todo.due_time, 'Due')}</p>}
            <p className={`status ${getStatusClass(todo.status)}`}>{todo.status}</p>
            <div className="todo-actions">
                <button onClick={() => updateStatus(todo.id)}>Toggle Status</button>
                <button onClick={() => openEditModal(todo)}>Edit</button>
                <button onClick={() => deleteTodo(todo.id)}>Delete</button>
            </div>
        </div>
    );
};

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        fetchTodos();
    }, []);

    const fetchTodos = async () => {
        try {
            const res = await axios.get('/api/todos');
            setTodos(res.data.sort((a, b) => new Date(a.due_time) - new Date(b.due_time)));
        } catch (err) {
            console.error(err);
        } finally {
            setLoading(false);
        }
    };

    const deleteTodo = async (id) => {
        try {
            await axios.delete(`/api/todos/${id}`);
            setTodos(todos.filter(todo => todo.id !== id));
        } catch (err) {
            console.error(err);
        }
    };

    const updateStatus = async (id) => {
        const todo = todos.find(t => t.id === id);
        if (!todo) return;
        const nextStatusIndex = (STATUS_OPTIONS.indexOf(todo.status) + 1) % STATUS_OPTIONS.length;
        const newStatus = STATUS_OPTIONS[nextStatusIndex];
        try {
            await axios.patch(`/api/todos/${id}`, { status: newStatus });
            setTodos(todos.map(t => t.id === id ? { ...t, status: newStatus } : t));
        } catch (err) {
            console.error(err);
        }
    };

    const openEditModal = (todo) => {
        console.log('Open edit modal for', todo);
    };

    if (loading) return <p>Loading...</p>;
    if (!todos.length) return <p>No todos found.</p>;

    return (
        <div className="todo-list">
            {todos.map(todo => (
                <TodoCard
                    key={todo.id}
                    todo={todo}
                    deleteTodo={deleteTodo}
                    updateStatus={updateStatus}
                    openEditModal={openEditModal}
                />
            ))}
        </div>
    );
};

export default TodoList;
