import { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TodoList.css';

const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);

    const pad = (num) => num.toString().padStart(2, '0');

    const year = date.getFullYear();
    const month = pad(date.getMonth() + 1);
    const day = pad(date.getDate());
    const hours = pad(date.getHours());
    const minutes = pad(date.getMinutes());
    const seconds = pad(date.getSeconds());

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
};

const isOverdue = (dueTime) => {
    if (!dueTime) return false;
    const taskDueDate = new Date(dueTime);
    const now = new Date();
    return taskDueDate < now;
};

const sortTodos = (currentTodos) => {
    return currentTodos.slice().sort((a, b) => {
        // 1. Primary Sort: Important tasks come first (true is smaller than false)
        if (a.is_important && !b.is_important) return -1;
        if (!a.is_important && b.is_important) return 1;

        // 2. Secondary Sort: If importance is the same, sort by date (closest date first)
        const dateA = new Date(a.due_time);
        const dateB = new Date(b.due_time);

        return dateA - dateB;
    });
};

function TodoList({ userId }) {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({
        title: '',
        description: '',
        dueDate: ''
    });
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                return;
            }
            try {
                const res = await axios.get('http://localhost:8080/todos', {
                    headers: { Authorization: `Bearer ${token}` }
                });

                const sortedTodos = sortTodos(res.data);
                setTodos(sortedTodos);
            } catch (err) {
                console.error('Error fetching todos:', err);
            }
        };
        fetchTodos();
    }, []);

    const addTodo = async (e) => {
        e.preventDefault();
        setError(null);
        const token = localStorage.getItem('token');

        if (!newTodo.title) {
            setError("Title cannot be empty.");
            return;
        }

        let effectiveDueTime;
        if (newTodo.dueDate) {
            effectiveDueTime = newTodo.dueDate.replace('T', ' ') + ':00';
        } else {
            effectiveDueTime = getOneWeekFromNow();
        }

        const todoPayload = {
            title: newTodo.title,
            description: newTodo.description,
            userId: userId,
            completed: false,
            due_time: effectiveDueTime,
            is_important: false, // Default to not important
        };

        try {
            const res = await axios.post('http://localhost:8080/todos', todoPayload, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (res.data && res.data.id) {
                const updatedTodos = sortTodos([...todos, res.data]);
                setTodos(updatedTodos);
                setNewTodo({ title: '', description: '', dueDate: '' });
            } else {
                setError("Todo added but response is invalid (missing ID). Check console.");
                console.error("Invalid API Response:", res.data);
            }

        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "An unknown error occurred.";
            setError(`Failed to add todo: ${errorMessage}`);
            console.error("Error adding todo:", err.response || err);
        }
    };

    const toggleImportance = async (id, currentImportance) => {
        const token = localStorage.getItem('token');
        setError(null);
        const newImportance = !currentImportance;

        try {
            const res = await axios.put(`http://localhost:8080/todos/${id}`, { is_important: newImportance }, {
                headers: { Authorization: `Bearer ${token}` }
            });

            const updatedTodo = res.data;

            const updatedList = todos.map(todo =>
                todo.id === id ? { ...todo, is_important: updatedTodo.is_important } : todo
            );

            const sortedTodos = sortTodos(updatedList);
            setTodos(sortedTodos);

        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "Update failed.";
            setError(`Failed to update importance: ${errorMessage}`);
            console.error("Error updating importance:", err.response || err);
        }
    };


    const deleteTodo = async (id) => {
        const token = localStorage.getItem('token');
        setError(null);

        try {
            await axios.delete(`http://localhost:8080/todos/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setTodos(todos.filter(todo => todo.id !== id));
        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "Deletion failed.";
            setError(`Failed to delete todo: ${errorMessage}`);
            console.error("Error deleting todo:", err.response || err);
        }
    };

    const formatDueTime = (timestamp) => {
        if (!timestamp) return 'No due time set';
        try {
            const date = new Date(timestamp);
            return date.toLocaleString();
        } catch {
            return timestamp;
        }
    };


    const completedClass = (isCompleted) =>
        isCompleted ? 'line-through done-task-text' : '';

    const filteredTodos = todos.filter(todo =>
        todo.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="todo-list">
            {error && <p style={{ color: 'red', fontWeight: 'bold' }}>{error}</p>}

            <input
                type="text"
                placeholder="Search tasks by title..."
                className="search-input"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            <form onSubmit={addTodo}>
                <input
                    type="text"
                    placeholder="Title"
                    value={newTodo.title}
                    onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
                />
                <textarea
                    placeholder="Description"
                    value={newTodo.description}
                    onChange={(e) => setNewTodo({ ...newTodo, description: e.target.value })}
                />
                <input
                    type="datetime-local"
                    title="Due Date and Time"
                    value={newTodo.dueDate}
                    onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
                    style={{ marginBottom: '10px' }}
                />
                <button type="submit">Add Todo</button>
            </form>
            <div className="todos">
                {filteredTodos.length === 0 && searchQuery && (
                    <p style={{ margin: '20px 0', textAlign: 'center' }}>
                        No tasks found matching "{searchQuery}".
                    </p>
                )}

                {filteredTodos.map(todo => {
                    const isTaskOverdue = isOverdue(todo.due_time) && !todo.completed;

                    return (
                        <div
                            key={todo.id}
                            className={`todo-item ${isTaskOverdue ? 'overdue-task' : ''} ${todo.is_important ? 'important-task' : ''}`}
                        >
                            <div className="todo-header">
                                <h3 className={completedClass(todo.completed)}>{todo.title}</h3>
                                <span
                                    onClick={() => toggleImportance(todo.id, todo.is_important)}
                                    style={{ cursor: 'pointer', fontSize: '20px', marginLeft: '10px' }}
                                    title={todo.is_important ? "Mark as Normal" : "Mark as Important"}
                                >
                                    {todo.is_important ? '⭐' : '☆'}
                                </span>
                            </div>

                            <p className={completedClass(todo.completed)}>{todo.description}</p>

                            {isTaskOverdue && (
                                <p className="overdue-text-flag">
                                    OVERDUE
                                </p>
                            )}

                            <small>Due: {formatDueTime(todo.due_time)}</small>
                            <br />

                            <button
                                onClick={() => deleteTodo(todo.id)}
                                style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}
                            >
                                Remove Task
                            </button>
                        </div>
                    )
                })}
            </div>
        </div>
    );
}

export default TodoList;
