import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useTheme } from '../context/ThemeContext';
import TodoList from './TodoList';
import '../styles/Dashboard.css';

// Using the correct internal reference ID for the uploaded image
// The file name change (to 'logo') on your PC doesn't change this internal ID.
const LOGO_URL = "uploaded:Gemini_Generated_Image_rm4v21rm4v21rm4v.jpg-52a553b5-d21d-48c3-9b24-12e7ecaebe54";

function Dashboard() {
    const navigate = useNavigate();
    const { theme, toggleTheme } = useTheme();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        const fetchProfile = async () => {
            try {
                const response = await axios.get('http://localhost:8080/user/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                setUser(response.data);
            } catch (error) {
                console.error('Failed to fetch user profile:', error);
                localStorage.removeItem('token');
                navigate('/login');
            }
        };

        fetchProfile();
    }, [navigate]);

    const displayUserName = user
        ? `${user.firstname || ''} ${user.name || ''}`.trim()
        : 'User';

    const getThemeIcon = () => {
        if (theme === 'light') return '‚òÄÔ∏è';
        if (theme === 'dark') return 'üåô';
        return 'üëì';
    };

    return (
        <div className={`dashboard ${theme}`}>
            <nav>
                <h1 style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <img
                        src={LOGO_URL}
                        // Alt text is kept, but its display is suppressed if the image fails to load
                        alt="e-todo logo"
                        style={{ height: '30px', width: 'auto' }}
                    />
                    Welcome, {displayUserName}!
                </h1>
                <div>
                    <button onClick={toggleTheme}>
                        {getThemeIcon()}
                    </button>
                    <button onClick={() => {
                        localStorage.removeItem('token');
                        navigate('/login');
                    }}>Logout</button>
                </div>
            </nav>
            {user ? (
                <TodoList userId={user.id} />
            ) : (
                <p>Loading your tasks...</p>
            )}
        </div>
    );
}

export default Dashboard;
