import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useTheme } from '../context/ThemeContext';
import TodoList from './TodoList';
import '../styles/Dashboard.css';

// Using the correct internal reference for the uploaded image
const LOGO_URL = "uploaded:Gemini_Generated_Image_rm4v21rm4v21rm4v.jpg-52a553b5-d21d-48c3-9b24-12e7ecaebe54";

// --- Custom Hook for User Service Logic ---

const useUserService = (navigate, setUser, setStatusMessage) => {
    const getToken = () => localStorage.getItem('token');

    const handleProfileUpdate = async (updatePayload, setProfileFormData) => {
        setStatusMessage({ type: '', text: 'Updating profile...' });
        const token = getToken();

        try {
            // NOTE: Ensure your backend has a PUT route at /user/profile
            const response = await axios.put('http://localhost:8080/user/profile', updatePayload, {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            setUser(response.data.user);
            setStatusMessage({ type: 'success', text: 'Profile updated successfully!' });
            setProfileFormData(prev => ({ ...prev, password: '' })); // Clear password field
            return true;
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Failed to update profile. (Is PUT /user/profile defined?)';
            setStatusMessage({ type: 'error', text: errorMessage });
            console.error('Profile update failed:', error.response || error);
            return false;
        }
    };

    const handleAccountDelete = async () => {
        if (!window.confirm("Are you absolutely sure you want to delete your account? This action cannot be undone.")) {
            return;
        }

        setStatusMessage({ type: '', text: 'Deleting account...' });
        const token = getToken();

        try {
            // NOTE: Ensure your backend has a DELETE route at /user/profile
            await axios.delete('http://localhost:8080/user/profile', {
                headers: { 'Authorization': `Bearer ${token}` }
            });

            localStorage.removeItem('token');
            navigate('/login');
        } catch (error) {
            const errorMessage = error.response?.data?.message || 'Failed to delete account.';
            setStatusMessage({ type: 'error', text: errorMessage });
            console.error('Account deletion failed:', error.response || error);
        }
    };

    return { handleProfileUpdate, handleAccountDelete };
};

// --- Dashboard Component ---

function Dashboard() {
    const navigate = useNavigate();
    const { theme, toggleTheme } = useTheme();
    const [user, setUser] = useState(null);
    const [isProfileModalOpen, setIsProfileModalOpen] = useState(false);
    const [profileFormData, setProfileFormData] = useState({
        firstname: '',
        name: '',
        password: ''
    });
    const [statusMessage, setStatusMessage] = useState({ type: '', text: '' });

    // Initialize custom service hook
    const { handleProfileUpdate, handleAccountDelete } = useUserService(navigate, setUser, setStatusMessage);


    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        const fetchProfile = async () => {
            try {
                const response = await axios.get('http://localhost:8080/user/profile', {
                    headers: { 'Authorization': `Bearer ${token}` }
                });

                setUser(response.data);
                // Initialize form data with current user info
                setProfileFormData({
                    firstname: response.data.firstname || '',
                    name: response.data.name || '',
                    password: ''
                });

            } catch (error) {
                console.error('Failed to fetch user profile:', error);
                localStorage.removeItem('token');
                navigate('/login');
            }
        };

        fetchProfile();
    }, [navigate]);

    // Handler for updating the profile form inputs
    const handleProfileChange = (e) => {
        setProfileFormData({ ...profileFormData, [e.target.name]: e.target.value });
    };

    // Wrapper function to call the hook's update logic
    const handleFormSubmit = (e) => {
        e.preventDefault();

        const updatePayload = {
            firstname: profileFormData.firstname,
            name: profileFormData.name,
        };
        if (profileFormData.password) {
            updatePayload.password = profileFormData.password;
        }

        handleProfileUpdate(updatePayload, setProfileFormData);
    };

    const displayUserName = user
        ? `${user.firstname || ''} ${user.name || ''}`.trim()
        : 'User';

    const getThemeIcon = () => {
        if (theme === 'light') return 'â˜€ï¸';
        if (theme === 'dark') return 'ðŸŒ™';
        return 'ðŸ‘“';
    };

    const ProfileModal = () => {
        if (!isProfileModalOpen || !user) return null;

        const statusStyle = statusMessage.type === 'error' ? { color: '#ef4444' } : { color: '#10b981' };

        return (
            <div style={{
                position: 'fixed', top: 0, left: 0, right: 0, bottom: 0,
                backgroundColor: 'rgba(0, 0, 0, 0.5)', zIndex: 1000,
                display: 'flex', justifyContent: 'center', alignItems: 'center'
            }}>
                <div style={{
                    backgroundColor: theme === 'dark' ? '#1f2937' : 'white',
                    padding: '2rem', borderRadius: '0.75rem', width: '90%', maxWidth: '500px',
                    boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1)'
                }}>
                    <h2 style={{ fontSize: '1.5rem', marginBottom: '1rem' }}>Manage Profile</h2>

                    <form onSubmit={handleFormSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '0.75rem' }}>
                        <input
                            type="text"
                            name="firstname"
                            placeholder="First Name"
                            value={profileFormData.firstname}
                            onChange={handleProfileChange}
                            required
                            style={{ padding: '0.5rem', borderRadius: '0.25rem', border: '1px solid #ccc', backgroundColor: theme === 'dark' ? '#374151' : 'white', color: theme === 'dark' ? 'white' : 'black' }}
                        />
                        <input
                            type="text"
                            name="name"
                            placeholder="Last Name"
                            value={profileFormData.name}
                            onChange={handleProfileChange}
                            required
                            style={{ padding: '0.5rem', borderRadius: '0.25rem', border: '1px solid #ccc', backgroundColor: theme === 'dark' ? '#374151' : 'white', color: theme === 'dark' ? 'white' : 'black' }}
                        />
                        <input
                            type="password"
                            name="password"
                            placeholder="New Password (optional)"
                            value={profileFormData.password}
                            onChange={handleProfileChange}
                            style={{ padding: '0.5rem', borderRadius: '0.25rem', border: '1px solid #ccc', backgroundColor: theme === 'dark' ? '#374151' : 'white', color: theme === 'dark' ? 'white' : 'black' }}
                        />
                        <p style={statusStyle}>{statusMessage.text}</p>
                        <button type="submit" style={{ padding: '0.75rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', backgroundColor: '#3b82f6', color: 'white', fontWeight: 'bold' }}>
                            Update Profile
                        </button>
                    </form>

                    <div style={{ marginTop: '1.5rem', paddingTop: '1rem', borderTop: '1px solid #4b5563' }}>
                        <h3 style={{ fontSize: '1.25rem', color: '#ef4444', marginBottom: '0.5rem' }}>Danger Zone</h3>
                        <p style={{ marginBottom: '0.5rem', fontSize: '0.875rem' }}>
                            Permanently delete your account and all associated data.
                        </p>
                        <button
                            onClick={handleAccountDelete} // Calls the hook function
                            style={{ padding: '0.5rem 1rem', borderRadius: '0.5rem', border: '1px solid #ef4444', cursor: 'pointer', backgroundColor: 'transparent', color: '#ef4444', fontWeight: 'bold' }}
                        >
                            Delete Account
                        </button>
                    </div>

                    <button
                        onClick={() => setIsProfileModalOpen(false)}
                        style={{ position: 'absolute', top: '10px', right: '10px', background: 'none', border: 'none', fontSize: '1.5rem', cursor: 'pointer', color: theme === 'dark' ? 'white' : 'black' }}
                    >
                        &times;
                    </button>
                </div>
            </div>
        );
    };

    return (
        <div className={`dashboard ${theme}`}>
            <nav style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', padding: '1rem 2rem' }}>
                <h1 style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
                    <img
                        src={LOGO_URL}
                        alt="e-todo logo"
                        style={{ height: '30px', width: 'auto' }}
                    />
                    Welcome, {displayUserName}!
                </h1>
                <div style={{ display: 'flex', gap: '10px' }}>
                    <button
                        onClick={() => { setIsProfileModalOpen(true); setStatusMessage({ type: '', text: '' }); }}
                        style={{ padding: '0.5rem 1rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', backgroundColor: '#4c4a4a', color: 'white' }}
                    >
                        Manage Profile
                    </button>
                    <button onClick={toggleTheme}>
                        {getThemeIcon()}
                    </button>
                    <button
                        onClick={() => {
                            localStorage.removeItem('token');
                            navigate('/login');
                        }}
                        style={{ padding: '0.5rem 1rem', borderRadius: '0.5rem', border: 'none', cursor: 'pointer', backgroundColor: '#ef4444', color: 'white' }}
                    >
                        Logout
                    </button>
                </div>
            </nav>
            {user ? (
                <TodoList userId={user.id} />
            ) : (
                <p style={{ textAlign: 'center', padding: '2rem' }}>Loading your tasks...</p>
            )}
            <ProfileModal />
        </div>
    );
}

export default Dashboard;
