import { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TodoList.css';

// Function to format the date for the backend (YYYY-MM-DD HH:MM:SS)
// This is now only used as a default if the user doesn't pick a time.
const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);
    return date.toISOString().slice(0, 19).replace('T', ' ');
};

function TodoList({ userId }) {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({
        title: '',
        description: '',
        // NEW: dueDate will store the date and time string from the input
        dueDate: ''
    });
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                return;
            }
            try {
                // Use correct port 8081 for fetching all todos
                const res = await axios.get('http://localhost:8081/todos', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTodos(res.data);
            } catch (err) {
                console.error('Error fetching todos:', err);
            }
        };
        fetchTodos();
    }, []);

    const addTodo = async (e) => {
        e.preventDefault();
        setError(null);
        const token = localStorage.getItem('token');

        if (!newTodo.title) {
            setError("Title cannot be empty.");
            return;
        }

        // Determine the due time: use the user-provided date/time, 
        // or fall back to one week from now if empty.
        const effectiveDueTime = newTodo.dueDate
            ? newTodo.dueDate.replace('T', ' ') // Format 'YYYY-MM-DDTHH:mm' to 'YYYY-MM-DD HH:mm'
            : getOneWeekFromNow();

        const todoPayload = {
            title: newTodo.title,
            description: newTodo.description,
            userId: userId,
            completed: false,
            due_time: effectiveDueTime, // Use the determined time
        };

        try {
            const res = await axios.post('http://localhost:8081/todos', todoPayload, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (res.data && res.data.id) {
                setTodos([...todos, res.data]);
                // Clear inputs, including the new date input
                setNewTodo({ title: '', description: '', dueDate: '' });
            } else {
                setError("Todo added but response is invalid (missing ID). Check console.");
                console.error("Invalid API Response:", res.data);
            }

        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "An unknown error occurred.";
            setError(`Failed to add todo: ${errorMessage}`);
            console.error("Error adding todo:", err.response || err);
        }
    };

    const deleteTodo = async (id) => {
        const token = localStorage.getItem('token');
        setError(null);

        try {
            await axios.delete(`http://localhost:8081/todos/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setTodos(todos.filter(todo => todo.id !== id));
        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "Deletion failed.";
            setError(`Failed to delete todo: ${errorMessage}`);
            console.error("Error deleting todo:", err.response || err);
        }
    };

    // Helper to format the displayed due time
    const formatDueTime = (timestamp) => {
        if (!timestamp) return 'No due time set';
        try {
            // Handle both date strings and Date objects
            const date = new Date(timestamp);
            return date.toLocaleString();
        } catch {
            return timestamp; // Fallback if formatting fails
        }
    };


    const completedClass = (isCompleted) =>
        isCompleted ? 'line-through done-task-text' : '';

    const filteredTodos = todos.filter(todo =>
        todo.title.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className="todo-list">
            {error && <p style={{ color: 'red', fontWeight: 'bold' }}>{error}</p>}

            <input
                type="text"
                placeholder="Search tasks by title..."
                className="search-input"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />

            <form onSubmit={addTodo}>
                <input
                    type="text"
                    placeholder="Title"
                    value={newTodo.title}
                    onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
                />
                <textarea
                    placeholder="Description"
                    value={newTodo.description}
                    onChange={(e) => setNewTodo({ ...newTodo, description: e.target.value })}
                />
                {/* NEW DATETIME INPUT */}
                <input
                    type="datetime-local"
                    title="Due Date and Time"
                    value={newTodo.dueDate}
                    onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
                    style={{ marginBottom: '10px' }}
                />
                <button type="submit">Add Todo</button>
            </form>
            <div className="todos">
                {filteredTodos.length === 0 && searchQuery && (
                    <p style={{ margin: '20px 0', textAlign: 'center' }}>
                        No tasks found matching "{searchQuery}".
                    </p>
                )}

                {filteredTodos.map(todo => (
                    <div key={todo.id} className="todo-item">
                        <h3 className={completedClass(todo.completed)}>{todo.title}</h3>
                        <p className={completedClass(todo.completed)}>{todo.description}</p>

                        {/* NEW: Display the due time */}
                        <small>Due: {formatDueTime(todo.due_time)}</small>
                        <br />

                        <button
                            onClick={() => deleteTodo(todo.id)}
                            style={{ backgroundColor: 'red', color: 'white', border: 'none', padding: '5px 10px', borderRadius: '4px', cursor: 'pointer', marginTop: '10px' }}
                        >
                            Remove Task
                        </button>
                    </div>
                ))}
            </div>
        </div>
    );
}

export default TodoList;
