import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { initializeApp } from 'firebase/app';
import { getAuth, signInAnonymously, signInWithCustomToken, onAuthStateChanged } from 'firebase/auth';
import { getFirestore, collection, onSnapshot, query, where, addDoc, updateDoc, deleteDoc, doc, Timestamp, setLogLevel } from 'firebase/firestore';

// --- CONFIGURATION & UTILITY FUNCTIONS ---

setLogLevel('Debug');
const STATUS_OPTIONS = ['not started', 'todo', 'in progress', 'done'];

// Converts Date object to Firestore Timestamp, or returns null
const dateToTimestamp = (dateString) => {
    if (!dateString) return null;
    try {
        // Append seconds if not present
        const dateTimeStr = dateString.includes(':00') ? dateString : `${dateString}:00`;
        const date = new Date(dateTimeStr.replace(' ', 'T') + 'Z'); // Treat as UTC for consistent parsing
        if (isNaN(date)) return null;
        return Timestamp.fromDate(date);
    } catch {
        return null;
    }
};

const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);
    return date.toISOString().substring(0, 16); // format: YYYY-MM-DDTHH:MM
};

const isOverdue = (dueTime) => {
    if (!dueTime) return false;
    const taskDueDate = dueTime instanceof Timestamp ? dueTime.toDate() : new Date(dueTime);
    return taskDueDate < new Date();
};

const sortTodos = (todos) => todos.slice().sort((a, b) => {
    const timeA = a.due_time?.toDate() || new Date(8640000000000000); // Max Date for null/invalid
    const timeB = b.due_time?.toDate() || new Date(8640000000000000);
    return timeA - timeB;
});

const formatTime = (timestamp, prefix) => {
    if (!timestamp) return '';
    try {
        const date = timestamp instanceof Timestamp ? timestamp.toDate() : new Date(timestamp);
        return `${prefix}: ${date.toLocaleDateString()} ${date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    } catch {
        return `${prefix}: Invalid Date`;
    }
};

const formatDueTimeForInput = (dueTime) => {
    if (!dueTime) return '';
    try {
        const date = dueTime instanceof Timestamp ? dueTime.toDate() : new Date(dueTime);
        // Format to YYYY-MM-DDTHH:MM for datetime-local input
        const year = date.getFullYear();
        const month = String(date.getMonth() + 1).padStart(2, '0');
        const day = String(date.getDate()).padStart(2, '0');
        const hours = String(date.getHours()).padStart(2, '0');
        const minutes = String(date.getMinutes()).padStart(2, '0');
        return `${year}-${month}-${day}T${hours}:${minutes}`;
    } catch {
        return '';
    }
};

// --- COMPONENTS ---

// 1. CONFIRM MODAL (Replaces window.confirm)
const ConfirmModal = ({ message, onConfirm, onCancel }) => {
    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
            <div className="bg-white p-6 rounded-xl shadow-2xl max-w-sm w-full">
                <p className="text-gray-700 mb-6 text-lg font-medium">{message}</p>
                <div className="flex justify-end space-x-3">
                    <button
                        onClick={onCancel}
                        className="px-4 py-2 text-sm font-semibold text-gray-700 bg-gray-200 rounded-lg hover:bg-gray-300 transition duration-150"
                    >
                        Cancel
                    </button>
                    <button
                        onClick={onConfirm}
                        className="px-4 py-2 text-sm font-semibold text-white bg-red-600 rounded-lg shadow-md hover:bg-red-700 transition duration-150"
                    >
                        Delete
                    </button>
                </div>
            </div>
        </div>
    );
};

// 2. EDIT FORM (The core fix for focus loss)
const EditTodoForm = ({ editingTodo, initialData, closeEditModal, onSave, statusOptions, error, setError }) => {
    // Local state for the form, isolated from the parent's re-renders
    const [formData, setFormData] = useState(initialData);

    // Sync local state when the editingTodo changes (i.e., when modal opens for a new task)
    useEffect(() => {
        setFormData(initialData);
    }, [initialData]);

    const handleEditChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });

    const handleSubmit = (e) => {
        e.preventDefault();
        setError('');
        if (!formData.title) return setError("Title cannot be empty.");
        onSave(editingTodo.id, formData);
    };

    return (
        <form onSubmit={handleSubmit} className="flex flex-col space-y-4">
            <label className="text-sm font-medium text-gray-700">Title</label>
            <input
                type="text"
                name="title"
                value={formData.title || ''}
                onChange={handleEditChange}
                required
                className="p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            />
            <label className="text-sm font-medium text-gray-700">Description</label>
            <textarea
                name="description"
                value={formData.description || ''}
                onChange={handleEditChange}
                className="p-2 border border-gray-300 rounded-lg h-20 resize-none focus:ring-blue-500 focus:border-blue-500"
            />
            <label className="text-sm font-medium text-gray-700">Due Date</label>
            <input
                type="datetime-local"
                name="dueDate"
                value={formData.dueDate || ''}
                onChange={handleEditChange}
                className="p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500"
            />
            <label className="text-sm font-medium text-gray-700">Status</label>
            <select
                name="status"
                value={formData.status || 'not started'}
                onChange={handleEditChange}
                className="p-2 border border-gray-300 rounded-lg focus:ring-blue-500 focus:border-blue-500 bg-white"
            >
                {statusOptions.map(status => <option key={status} value={status}>{status.charAt(0).toUpperCase() + status.slice(1)}</option>)}
            </select>
            {error && <p className="text-red-500 text-sm mt-2">{error}</p>}
            <div className="flex justify-end space-x-3 pt-4">
                <button
                    type="button"
                    onClick={closeEditModal}
                    className="px-4 py-2 text-sm font-semibold text-gray-700 bg-gray-200 rounded-lg hover:bg-gray-300 transition duration-150"
                >
                    Cancel
                </button>
                <button
                    type="submit"
                    className="px-4 py-2 text-sm font-semibold text-white bg-green-600 rounded-lg shadow-md hover:bg-green-700 transition duration-150"
                >
                    Save Changes
                </button>
            </div>
        </form>
    );
};

const TodoCard = ({ todo, openConfirmDelete, openEditModal }) => {
    const isDone = todo.status === 'done';
    const isTaskOverdue = isOverdue(todo.due_time) && !isDone;
    const completedClass = isDone ? 'line-through text-gray-500' : 'text-gray-900';

    const statusColor = {
        'not started': 'bg-gray-100 border-gray-300',
        todo: 'bg-blue-50 border-blue-400',
        'in progress': 'bg-yellow-50 border-yellow-400',
        done: 'bg-green-50 border-green-400'
    }[todo.status] || 'bg-gray-100 border-gray-300';

    const handleDragStart = (e) => {
        // Data transferred is the Firestore document ID
        e.dataTransfer.setData('todoId', todo.id);
        e.currentTarget.classList.add('opacity-50');
    }
    const handleDragEnd = (e) => e.currentTarget.classList.remove('opacity-50');

    return (
        <div
            className={`todo-item p-4 mb-3 rounded-xl border-l-4 shadow-sm cursor-grab ${statusColor} 
                ${isTaskOverdue ? 'border-red-600 bg-red-50' : ''}`}
            draggable
            onDragStart={handleDragStart}
            onDragEnd={handleDragEnd}
        >
            <div className="card-content">
                <h3 className={`font-semibold text-lg truncate ${completedClass}`}>{todo.title}</h3>
                <p className={`text-sm mt-1 mb-2 ${completedClass}`}>{todo.description}</p>
                {isTaskOverdue && <p className="text-xs font-bold text-red-600 animate-pulse">OVERDUE</p>}
                <small className={`text-xs block ${isTaskOverdue ? 'text-red-500' : 'text-gray-500'}`}>{formatTime(todo.due_time, 'Due')}</small>
                <small className="text-xs text-gray-400 block mt-1">{formatTime(todo.created_at, 'Created')}</small>
            </div>
            <div className="flex justify-end space-x-2 mt-3">
                <button
                    onClick={() => openEditModal(todo)}
                    className="text-xs px-3 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600 transition duration-150 shadow-sm"
                >
                    Edit
                </button>
                <button
                    onClick={() => openConfirmDelete(todo.id, todo.title)}
                    className="text-xs px-3 py-1 bg-red-500 text-white rounded-md hover:bg-red-600 transition duration-150 shadow-sm"
                >
                    Remove
                </button>
            </div>
        </div>
    );
};

const KanbanColumn = ({ status, todos, updateStatus, openConfirmDelete, openEditModal }) => {
    const handleDrop = (e) => {
        e.preventDefault();
        const todoId = e.dataTransfer.getData('todoId');
        if (todoId) updateStatus(todoId, status);
    };
    const handleDragOver = (e) => e.preventDefault();

    const displayHeader = {
        'not started': 'Ideas / Backlog',
        todo: 'To Do',
        'in progress': 'In Progress',
        done: 'Done'
    }[status] || status;

    const columnColor = {
        'not started': 'bg-gray-50 border-gray-300',
        todo: 'bg-blue-50 border-blue-400',
        'in progress': 'bg-yellow-50 border-yellow-400',
        done: 'bg-green-50 border-green-400'
    }[status] || 'bg-gray-50 border-gray-300';

    return (
        <div
            className={`flex-1 min-w-[280px] max-w-[320px] p-4 rounded-xl shadow-lg border-t-4 ${columnColor} flex flex-col h-full`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
        >
            <h2 className="text-lg font-bold mb-4 border-b pb-2 text-gray-800 flex justify-between items-center">
                {displayHeader} <span className="text-sm font-medium px-2 py-0.5 rounded-full bg-white shadow-inner">{todos.length}</span>
            </h2>
            <div className="column-tasks space-y-3 overflow-y-auto flex-grow pr-1">
                {todos.map(todo => (
                    <TodoCard
                        key={todo.id}
                        todo={todo}
                        openConfirmDelete={openConfirmDelete}
                        openEditModal={openEditModal}
                    />
                ))}
            </div>
        </div>
    );
};


// 3. MAIN APP COMPONENT
export default function App() {
    // --- FIREBASE AND AUTH STATE ---
    const [db, setDb] = useState(null);
    const [auth, setAuth] = useState(null);
    const [userId, setUserId] = useState(null);
    const [isAuthReady, setIsAuthReady] = useState(false);

    // --- APPLICATION STATE ---
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({ title: '', description: '', dueDate: '' });
    const [globalError, setGlobalError] = useState('');
    const [modalError, setModalError] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    // Editing/Modal State
    const [editingTodo, setEditingTodo] = useState(null);
    const [editFormData, setEditFormData] = useState({});

    // Confirmation Modal State
    const [deleteCandidate, setDeleteCandidate] = useState(null); // { id, title }

    // Initialize Firebase and Auth
    useEffect(() => {
        const appId = typeof __app_id !== 'undefined' ? __app_id : 'default-app-id';
        try {
            const firebaseConfig = JSON.parse(typeof __firebase_config !== 'undefined' ? __firebase_config : '{}');
            const app = initializeApp(firebaseConfig);
            const firestore = getFirestore(app);
            const authInstance = getAuth(app);

            setDb(firestore);
            setAuth(authInstance);

            const unsubscribe = onAuthStateChanged(authInstance, async (user) => {
                if (!user) {
                    const token = typeof __initial_auth_token !== 'undefined' ? __initial_auth_token : null;
                    if (token) {
                        await signInWithCustomToken(authInstance, token);
                    } else {
                        await signInAnonymously(authInstance);
                    }
                }
                setUserId(authInstance.currentUser?.uid || crypto.randomUUID());
                setIsAuthReady(true);
            });
            return () => unsubscribe();
        } catch (e) {
            setGlobalError(`Firebase Initialization failed: ${e.message}`);
        }
    }, []);

    // Real-time Data Listener
    useEffect(() => {
        if (!isAuthReady || !db || !userId) return;

        const todosCollectionRef = collection(db, 'artifacts', userId, 'todos');

        const unsubscribe = onSnapshot(todosCollectionRef,
            (snapshot) => {
                const fetchedTodos = snapshot.docs.map(doc => ({
                    id: doc.id,
                    ...doc.data()
                }));
                setTodos(sortTodos(fetchedTodos));
                setGlobalError(''); // Clear global errors on successful fetch
            },
            (error) => {
                setGlobalError(`Failed to fetch tasks: ${error.message}`);
            }
        );

        return () => unsubscribe();
    }, [isAuthReady, db, userId]);

    // --- CRUD OPERATIONS (Firestore Implementation) ---

    const addTodo = async (e) => {
        e.preventDefault();
        setGlobalError('');
        if (!newTodo.title || !db || !userId) return setGlobalError("Task title is required.");

        const effectiveDueTime = newTodo.dueDate ? dateToTimestamp(newTodo.dueDate) : dateToTimestamp(getOneWeekFromNow());

        const todoPayload = {
            title: newTodo.title,
            description: newTodo.description,
            userId: userId,
            status: 'not started',
            due_time: effectiveDueTime,
            created_at: Timestamp.now()
        };

        try {
            const todosCollectionRef = collection(db, 'artifacts', userId, 'todos');
            await addDoc(todosCollectionRef, todoPayload);
            setNewTodo({ title: '', description: '', dueDate: '' });
        } catch (err) {
            setGlobalError(`Failed to add task: ${err.message}`);
        }
    };

    const updateStatus = async (id, newStatus) => {
        if (!db || !userId) return setGlobalError("Authentication not ready.");
        setGlobalError('');

        try {
            const docRef = doc(db, 'artifacts', userId, 'todos', id);
            await updateDoc(docRef, { status: newStatus });
        } catch (err) {
            setGlobalError(`Failed to update status for task ${id}: ${err.message}`);
        }
    };

    const deleteTodo = async (id) => {
        if (!db || !userId) return setGlobalError("Authentication not ready.");
        setGlobalError('');
        setDeleteCandidate(null); // Close the confirmation modal

        try {
            const docRef = doc(db, 'artifacts', userId, 'todos', id);
            await deleteDoc(docRef);
        } catch (err) {
            setGlobalError(`Failed to delete task: ${err.message}`);
        }
    };

    const openConfirmDelete = (id, title) => {
        setDeleteCandidate({ id, title });
    };

    // Handler for successful form submission from EditTodoForm
    const handleEditSave = async (todoId, formData) => {
        if (!db || !userId) return setModalError("Authentication not ready.");
        setModalError('');

        const effectiveDueTime = formData.dueDate ? dateToTimestamp(formData.dueDate) : null;

        const updatePayload = {
            title: formData.title,
            description: formData.description,
            status: formData.status,
            due_time: effectiveDueTime
        };

        try {
            const docRef = doc(db, 'artifacts', userId, 'todos', todoId);
            await updateDoc(docRef, updatePayload);
            setEditingTodo(null); // Close modal on success
        } catch (err) {
            setModalError(`Failed to update task: ${err.message}`);
        }
    };

    // --- MODAL & FILTER LOGIC ---

    const openEditModal = (todo) => {
        setEditingTodo(todo);
        // Prepare initial data for the isolated form component
        const initialData = {
            title: todo.title,
            description: todo.description,
            // Convert Firestore Timestamp to input string format
            dueDate: formatDueTimeForInput(todo.due_time),
            status: todo.status
        };
        setEditFormData(initialData);
        setModalError('');
    };

    const closeEditModal = () => {
        setEditingTodo(null);
        setEditFormData({});
    };

    const filteredTodos = useMemo(() => {
        const query = searchQuery.toLowerCase();
        if (!query) return todos;
        return todos.filter(todo =>
            todo.title.toLowerCase().includes(query) ||
            (todo.description && todo.description.toLowerCase().includes(query))
        );
    }, [todos, searchQuery]);

    const groupedTodos = useMemo(() => {
        return filteredTodos.reduce((acc, todo) => {
            const status = todo.status || 'not started';
            if (!acc[status]) acc[status] = [];
            acc[status].push(todo);
            return acc;
        }, {});
    }, [filteredTodos]);

    const EditTodoModal = () => {
        if (!editingTodo) return null;
        return (
            <div className="fixed inset-0 bg-black bg-opacity-60 flex items-center justify-center z-40 p-4" onClick={closeEditModal}>
                <div
                    className="bg-white p-6 rounded-xl shadow-2xl max-w-lg w-full transform transition-all duration-300 scale-100"
                    onClick={e => e.stopPropagation()}
                >
                    <h2 className="text-2xl font-bold mb-4 border-b pb-2 text-gray-800">Edit Task: {editingTodo.title}</h2>
                    <EditTodoForm
                        editingTodo={editingTodo}
                        initialData={editFormData}
                        closeEditModal={closeEditModal}
                        onSave={handleEditSave}
                        statusOptions={STATUS_OPTIONS}
                        error={modalError}
                        setError={setModalError}
                    />
                </div>
            </div>
        );
    };

    // --- RENDER ---
    const allStatuses = [...STATUS_OPTIONS, ...Object.keys(groupedTodos).filter(s => !STATUS_OPTIONS.includes(s))];

    if (!isAuthReady) {
        return <div className="p-8 text-center text-xl font-semibold text-blue-600">Loading Application...</div>;
    }

    return (
        <div className="min-h-screen bg-gray-50 p-4 md:p-8 font-sans">
            <h1 className="text-3xl font-extrabold text-gray-900 mb-6 border-b pb-2">Kanban Task Board</h1>
            <p className="text-sm text-gray-600 mb-4">User ID: <span className="font-mono bg-gray-200 p-1 rounded-md">{userId || 'N/A'}</span></p>

            {globalError && (
                <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">
                    <span className="block sm:inline">{globalError}</span>
                </div>
            )}

            {/* Controls and Form */}
            <div className="bg-white p-6 rounded-xl shadow-lg mb-6 flex flex-col lg:flex-row lg:space-x-6 space-y-4 lg:space-y-0">
                <input
                    type="text"
                    placeholder="Search tasks by title or description..."
                    className="p-3 border border-gray-300 rounded-lg flex-1 focus:ring-blue-500 focus:border-blue-500 transition duration-150"
                    value={searchQuery}
                    onChange={e => setSearchQuery(e.target.value)}
                />

                <form onSubmit={addTodo} className="flex flex-col md:flex-row md:space-x-3 space-y-3 md:space-y-0 w-full lg:w-2/3">
                    <input
                        type="text"
                        placeholder="Task Title (Required)"
                        value={newTodo.title}
                        onChange={e => setNewTodo({ ...newTodo, title: e.target.value })}
                        required
                        className="p-3 border border-gray-300 rounded-lg flex-1 focus:ring-indigo-500 focus:border-indigo-500"
                    />
                    <textarea
                        placeholder="Description (Optional)"
                        value={newTodo.description}
                        onChange={e => setNewTodo({ ...newTodo, description: e.target.value })}
                        className="p-3 border border-gray-300 rounded-lg flex-1 resize-none h-10 md:h-auto focus:ring-indigo-500 focus:border-indigo-500"
                    />
                    <input
                        type="datetime-local"
                        title="Due Date and Time"
                        value={newTodo.dueDate}
                        onChange={e => setNewTodo({ ...newTodo, dueDate: e.target.value })}
                        className="p-3 border border-gray-300 rounded-lg focus:ring-indigo-500 focus:border-indigo-500"
                    />
                    <button
                        type="submit"
                        className="p-3 bg-indigo-600 text-white font-semibold rounded-lg shadow-md hover:bg-indigo-700 transition duration-150 transform hover:scale-[1.02] active:scale-[0.98] whitespace-nowrap"
                        disabled={!db}
                    >
                        Add Task
                    </button>
                </form>
            </div>

            {/* Kanban Board */}
            <div className="kanban-board flex flex-row space-x-4 overflow-x-auto pb-4 h-[75vh]">
                {allStatuses.map(status => (
                    <KanbanColumn
                        key={status}
                        status={status}
                        todos={groupedTodos[status] || []}
                        updateStatus={updateStatus}
                        openConfirmDelete={openConfirmDelete}
                        openEditModal={openEditModal}
                    />
                ))}
            </div>

            <EditTodoModal />

            {/* Confirmation Modal */}
            {deleteCandidate && (
                <ConfirmModal
                    message={`Are you sure you want to delete the task: "${deleteCandidate.title}"? This cannot be undone.`}
                    onConfirm={() => deleteTodo(deleteCandidate.id)}
                    onCancel={() => setDeleteCandidate(null)}
                />
            )}
        </div>
    );
}
