import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/TodoList.css';

const STATUS_OPTIONS = ['not started', 'todo', 'in progress', 'done'];

const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);

    const pad = (num) => num.toString().padStart(2, '0');

    const year = date.getFullYear();
    const month = pad(date.getMonth() + 1);
    const day = pad(date.getDate());
    const hours = pad(date.getHours());
    const minutes = pad(date.getMinutes());
    const seconds = pad(date.getSeconds());

    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
};

const isOverdue = (dueTime) => {
    if (!dueTime) return false;
    const taskDueDate = new Date(dueTime);
    const now = new Date();
    return taskDueDate < now;
};

const sortTodos = (currentTodos) => {
    return currentTodos.slice().sort((a, b) => {
        const dateA = new Date(a.due_time);
        const dateB = new Date(b.due_time);
        return dateA - dateB;
    });
};

const formatTime = (timestamp, prefix) => {
    if (!timestamp) return '';
    try {
        const date = new Date(timestamp);
        return `${prefix}: ${date.toLocaleDateString()} ${date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
    } catch {
        return `${prefix}: Invalid Date`;
    }
};

const formatDueTimeForInput = (dueTime) => {
    if (!dueTime) return '';
    try {
        const datePart = dueTime.split(' ')[0];
        const timePart = dueTime.split(' ')[1].substring(0, 5);
        return `${datePart}T${timePart}`;
    } catch {
        return '';
    }
};

const TodoCard = ({ todo, deleteTodo, updateStatus, openEditModal }) => {
    const isDone = todo.status === 'done';
    const isTaskOverdue = isOverdue(todo.due_time) && !isDone;

    const completedClass = isDone ? 'line-through done-task-text' : '';

    const getStatusClass = (status) => {
        switch (status) {
            case 'done':
                return 'done-task';
            case 'in progress':
                return 'in-progress-task';
            case 'todo':
                return 'todo-task';
            default:
                return '';
        }
    };

    const statusClass = getStatusClass(todo.status);

    const handleDragStart = (e) => {
        e.dataTransfer.setData('todoId', todo.id);
    };

    return (
        <div
            className={`todo-item ${isTaskOverdue ? 'overdue-task' : ''} ${statusClass}`}
            draggable
            onDragStart={handleDragStart}
        >
            <div className="card-content">
                <h3 className={completedClass}>{todo.title}</h3>

                <p className={completedClass}>{todo.description}</p>

                {isTaskOverdue && (
                    <p className="overdue-text-flag">OVERDUE</p>
                )}

                <small className="card-small-text">{formatTime(todo.due_time, 'Due')}</small>
                <br />
                <small className="created-at-text card-small-text">{formatTime(todo.created_at, 'Created')}</small>
            </div>

            <div className="card-actions">
                <button
                    onClick={() => openEditModal(todo)}
                    className="edit-button"
                >
                    Edit
                </button>
                <button
                    onClick={() => deleteTodo(todo.id)}
                    className="delete-button"
                >
                    Remove
                </button>
            </div>
        </div>
    );
};

const KanbanColumn = ({ status, todos, updateStatus, deleteTodo, openEditModal }) => {
    const handleDrop = (e) => {
        e.preventDefault();
        const todoId = e.dataTransfer.getData('todoId');
        if (todoId) {
            updateStatus(parseInt(todoId, 10), status);
        }
    };

    const handleDragOver = (e) => {
        e.preventDefault();
    };

    const getDisplayHeader = (currentStatus) => {
        switch (currentStatus) {
            case 'not started':
                return 'Ideas';
            case 'todo':
                return 'To Do';
            case 'in progress':
                return 'In Progress';
            case 'done':
                return 'Done';
            default:
                return currentStatus.charAt(0).toUpperCase() + currentStatus.slice(1);
        }
    };

    return (
        <div
            className={`kanban-column ${status.replace(/\s/g, '-')}`}
            onDrop={handleDrop}
            onDragOver={handleDragOver}
        >
            <h2>{getDisplayHeader(status)} ({todos.length})</h2>
            <div className="column-tasks">
                {todos.map(todo => (
                    <TodoCard
                        key={todo.id}
                        todo={todo}
                        updateStatus={updateStatus}
                        deleteTodo={deleteTodo}
                        openEditModal={openEditModal}
                    />
                ))}
            </div>
        </div>
    );
};


function TodoList({ userId }) {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({
        title: '',
        description: '',
        dueDate: ''
    });
    const [editingTodo, setEditingTodo] = useState(null);
    const [editFormData, setEditFormData] = useState({});
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');

    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem('token');
            if (!token) {
                return;
            }
            try {
                const res = await axios.get('http://localhost:8080/todos', {
                    headers: { Authorization: `Bearer ${token}` }
                });

                const sortedTodos = sortTodos(res.data);
                setTodos(sortedTodos);
            } catch (err) {
                console.error('Error fetching todos:', err);
            }
        };
        fetchTodos();
    }, []);

    const openEditModal = (todo) => {
        setEditingTodo(todo);
        setEditFormData({
            title: todo.title,
            description: todo.description,
            dueDate: formatDueTimeForInput(todo.due_time),
            status: todo.status
        });
        setError(null);
    };

    const closeEditModal = () => {
        setEditingTodo(null);
        setEditFormData({});
    };

    const handleEditChange = (e) => {
        setEditFormData({ ...editFormData, [e.target.name]: e.target.value });
    };

    const editTodo = async (e) => {
        e.preventDefault();
        if (!editingTodo) return;
        setError(null);
        const token = localStorage.getItem('token');
        const todoId = editingTodo.id;

        if (!editFormData.title) {
            setError("Title cannot be empty.");
            return;
        }

        let effectiveDueTime;
        if (editFormData.dueDate) {
            effectiveDueTime = editFormData.dueDate.replace('T', ' ') + ':00';
        } else {
            effectiveDueTime = getOneWeekFromNow();
        }

        const updatedTodoLocal = {
            ...editingTodo,
            title: editFormData.title,
            description: editFormData.description,
            status: editFormData.status,
            due_time: effectiveDueTime,
        };

        const updatePayload = {
            title: editFormData.title,
            description: editFormData.description,
            status: editFormData.status,
            due_time: effectiveDueTime,
        };

        try {
            const res = await axios.put(`http://localhost:8080/todos/${todoId}`, updatePayload, {
                headers: { Authorization: `Bearer ${token}` }
            });

            const serverReturnedData = res.data && res.data.id ? res.data : updatedTodoLocal;

            if (serverReturnedData.id) {
                const updatedTodos = todos.map(todo =>
                    todo.id === todoId ? serverReturnedData : todo
                );
                setTodos(sortTodos(updatedTodos));
                closeEditModal();

                if (!res.data || !res.data.id) {
                    console.warn("API updated successfully but returned minimal data. Using locally constructed object for state update.");
                }

            } else {
                setError("Update successful but API response is completely missing the item's ID. Check console.");
                console.error("Invalid API Response: No ID found in data.", res.data);
            }
        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "An unknown error occurred.";
            setError(`Failed to update todo: ${errorMessage}`);
            console.error("Error updating todo:", err.response || err);
        }
    };


    const addTodo = async (e) => {
        e.preventDefault();
        setError(null);
        const token = localStorage.getItem('token');

        if (!newTodo.title) {
            setError("Title cannot be empty.");
            return;
        }

        let effectiveDueTime;
        if (newTodo.dueDate) {
            effectiveDueTime = newTodo.dueDate.replace('T', ' ') + ':00';
        } else {
            effectiveDueTime = getOneWeekFromNow();
        }

        const todoPayload = {
            title: newTodo.title,
            description: newTodo.description,
            userId: userId,
            status: 'not started',
            due_time: effectiveDueTime,
        };

        try {
            const res = await axios.post('http://localhost:8080/todos', todoPayload, {
                headers: { Authorization: `Bearer ${token}` }
            });

            if (res.data && res.data.id) {
                const updatedTodos = sortTodos([...todos, res.data]);
                setTodos(updatedTodos);
                setNewTodo({ title: '', description: '', dueDate: '' });
            } else {
                setError("Todo added but response is invalid (missing ID). Check console.");
                console.error("Invalid API Response:", res.data);
            }

        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "An unknown error occurred.";
            setError(`Failed to add todo: ${errorMessage}`);
            console.error("Error adding todo:", err.response || err);
        }
    };

    const updateStatus = async (id, newStatus) => {
        const token = localStorage.getItem('token');
        setError(null);

        const updatedTodos = todos.map(todo =>
            todo.id === id ? { ...todo, status: newStatus } : todo
        );
        setTodos(updatedTodos);

        try {
            await axios.put(`http://localhost:8080/todos/${id}`,
                { status: newStatus },
                { headers: { Authorization: `Bearer ${token}` } }
            );
        } catch (err) {
            setError(`Failed to update status: ${err.response?.data?.message || err.message}`);
            const fetchTodos = async () => {
                const res = await axios.get('http://localhost:8080/todos', {
                    headers: { Authorization: `Bearer ${token}` }
                });
                setTodos(sortTodos(res.data));
            };
            fetchTodos();
        }
    };

    const deleteTodo = async (id) => {
        const token = localStorage.getItem('token');
        setError(null);

        if (!window.confirm("Are you sure you want to delete this task?")) {
            return;
        }

        try {
            await axios.delete(`http://localhost:8080/todos/${id}`, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setTodos(todos.filter(todo => todo.id !== id));
        } catch (err) {
            const errorMessage = err.response?.data?.message || err.message || "Deletion failed.";
            setError(`Failed to delete todo: ${errorMessage}`);
            console.error("Error deleting todo:", err.response || err);
        }
    };

    const filteredTodos = todos.filter(todo =>
        todo.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (todo.description && todo.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    const groupedTodos = filteredTodos.reduce((acc, todo) => {
        const status = todo.status || 'not started';
        if (!acc[status]) {
            acc[status] = [];
        }
        acc[status].push(todo);
        return acc;
    }, {});


    const EditTodoModal = () => {
        if (!editingTodo) return null;

        return (
            <div className="modal-overlay" onClick={closeEditModal}>
                <div
                    className="modal-content"
                    onClick={e => e.stopPropagation()}
                    // Adding the key here forces React to treat this modal instance as stable 
                    // and not remount the entire form on every state change (keystroke).
                    key={editingTodo.id}
                >
                    <h2>Edit Task: {editingTodo.title}</h2>

                    <form onSubmit={editTodo} className="edit-todo-form">
                        <label>Title</label>
                        <input
                            type="text"
                            name="title"
                            value={editFormData.title || ''}
                            onChange={handleEditChange}
                            required
                        />

                        <label>Description</label>
                        <textarea
                            name="description"
                            value={editFormData.description || ''}
                            onChange={handleEditChange}
                        />

                        <label>Due Date</label>
                        <input
                            type="datetime-local"
                            name="dueDate"
                            value={editFormData.dueDate || ''}
                            onChange={handleEditChange}
                            title="Due Date and Time"
                        />

                        <label>Status</label>
                        <select
                            name="status"
                            value={editFormData.status || 'not started'}
                            onChange={handleEditChange}
                        >
                            {STATUS_OPTIONS.map(status => (
                                <option key={status} value={status}>{status.charAt(0).toUpperCase() + status.slice(1)}</option>
                            ))}
                        </select>

                        {error && <p className="modal-error">{error}</p>}

                        <div className="modal-actions">
                            <button type="button" onClick={closeEditModal} className="cancel-button">
                                Cancel
                            </button>
                            <button type="submit" className="save-button">
                                Save Changes
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        );
    };


    return (
        <div className="kanban-board-container">
            {error && <p className="global-error-message">{error}</p>}

            <div className="controls-and-form">
                <input
                    type="text"
                    placeholder="Search tasks by title or description..."
                    className="search-input"
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                />

                <form onSubmit={addTodo} className="add-todo-form">
                    <input
                        type="text"
                        placeholder="Title (Required)"
                        value={newTodo.title}
                        onChange={(e) => setNewTodo({ ...newTodo, title: e.target.value })}
                        required
                    />
                    <textarea
                        placeholder="Description (Optional)"
                        value={newTodo.description}
                        onChange={(e) => setNewTodo({ ...newTodo, description: e.target.value })}
                    />
                    <input
                        type="datetime-local"
                        title="Due Date and Time"
                        value={newTodo.dueDate}
                        onChange={(e) => setNewTodo({ ...newTodo, dueDate: e.target.value })}
                    />
                    <button type="submit">
                        Add Task
                    </button>
                </form>
            </div>

            <div className="kanban-board">
                {STATUS_OPTIONS.map(status => (
                    <KanbanColumn
                        key={status}
                        status={status}
                        todos={groupedTodos[status] || []}
                        updateStatus={updateStatus}
                        deleteTodo={deleteTodo}
                        openEditModal={openEditModal}
                    />
                ))}

                {Object.keys(groupedTodos).filter(s => !STATUS_OPTIONS.includes(s)).map(status => (
                    <KanbanColumn
                        key={status}
                        status={status}
                        todos={groupedTodos[status]}
                        updateStatus={updateStatus}
                        deleteTodo={deleteTodo}
                        openEditModal={openEditModal}
                    />
                ))}
            </div>

            <EditTodoModal />
        </div>
    );
}

export default TodoList;
