import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import '../styles/Register.css';

function Register() {
    const navigate = useNavigate();
    const [formData, setFormData] = useState({
        email: '',
        password: '',
        name: '',
        firstname: ''
    });
    // State for handling messages
    const [message, setMessage] = useState('');
    const [isError, setIsError] = useState(false);

    const handleChange = (e) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        setMessage(''); // Clear previous message
        setIsError(false);

        try {
            // Note: Data fields match the backend expectation
            const response = await axios.post('http://localhost:8080/auth/register', formData);

            // Success: Display success message and navigate
            setMessage(response.data.message || 'Registration successful! Redirecting...');

            // Use setTimeout instead of alert()
            setTimeout(() => {
                navigate('/login');
            }, 1000);

        } catch (error) {
            console.error('Registration failed:', error.response || error);
            setIsError(true);

            // Display detailed error from backend or a fallback
            const errorMessage = error.response?.data?.message || 'Registration failed. Check server status.';
            setMessage(errorMessage);
        }
    };

    return (
        <div className="register-container">
            <form onSubmit={handleSubmit}>
                <h2>Register</h2>
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    value={formData.email}
                    onChange={handleChange}
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={formData.password}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="name"
                    placeholder="Last Name"
                    value={formData.name}
                    onChange={handleChange}
                />
                <input
                    type="text"
                    name="firstname"
                    placeholder="First Name"
                    value={formData.firstname}
                    onChange={handleChange}
                />
                <button type="submit">Register</button>
                <p>
                    Already have an account?
                    <span onClick={() => navigate('/login')} className="link">
                        Login here
                    </span>
                </p>
            </form>
            {/* Display message based on state */}
            {message && (
                <p style={{ color: isError ? 'red' : 'green', fontWeight: 'bold', marginTop: '10px' }}>
                    {message}
                </p>
            )}
        </div>
    );
}

export default Register;
