import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate, BrowserRouter, Routes, Route } from 'react-router-dom';

function TodoList({ userId }) {
    const [tasks, setTasks] = useState([]);
    const [newTask, setNewTask] = useState('');

    useEffect(() => {
        setTasks([
            { id: 1, text: "Finish welcome screen fix", completed: true },
            { id: 2, text: "Implement task persistence", completed: false },
        ]);
    }, [userId]);

    const handleAddTask = (e) => {
        e.preventDefault();
        if (newTask.trim()) {
            setTasks([...tasks, { id: Date.now(), text: newTask.trim(), completed: false }]);
            setNewTask('');
        }
    };

    const toggleTask = useCallback((id) => {
        setTasks(prevTasks =>
            prevTasks.map(task =>
                task.id === id ? { ...task, completed: !task.completed } : task
            )
        );
    }, []);

    return (
        <div className="p-6 max-w-xl mx-auto bg-white dark:bg-gray-800 rounded-xl shadow-lg mt-8">
            <h2 className="text-2xl font-semibold mb-4 text-gray-800 dark:text-white">Your Tasks</h2>
            <form onSubmit={handleAddTask} className="flex space-x-2 mb-6">
                <input
                    type="text"
                    value={newTask}
                    onChange={(e) => setNewTask(e.target.value)}
                    placeholder="Add a new todo..."
                    className="flex-grow p-2 border border-gray-300 dark:border-gray-600 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500 dark:bg-gray-700 dark:text-white"
                />
                <button
                    type="submit"
                    className="px-4 py-2 bg-indigo-600 text-white font-medium rounded-lg hover:bg-indigo-700 transition duration-150 shadow-md"
                >
                    Add
                </button>
            </form>
            <ul className="space-y-3">
                {tasks.map(task => (
                    <li key={task.id}
                        className={`flex items-center justify-between p-3 rounded-lg transition duration-150 cursor-pointer ${task.completed
                            ? 'bg-green-100 dark:bg-green-800 line-through text-gray-500 dark:text-gray-400'
                            : 'bg-gray-50 dark:bg-gray-700 text-gray-900 dark:text-white hover:bg-gray-100 dark:hover:bg-gray-600'
                            }`}
                        onClick={() => toggleTask(task.id)}
                    >
                        <span>{task.text}</span>
                        <input
                            type="checkbox"
                            checked={task.completed}
                            readOnly
                            className="h-4 w-4 text-indigo-600 border-gray-300 rounded focus:ring-indigo-500 cursor-pointer"
                            onClick={(e) => e.stopPropagation()}
                        />
                    </li>
                ))}
                {tasks.length === 0 && <p className="text-center text-gray-500 dark:text-gray-400">No tasks yet!</p>}
            </ul>
        </div>
    );
}

function Login() {
    const navigate = useNavigate();

    useEffect(() => {
        if (localStorage.getItem('token')) {
            navigate('/');
        }
    }, [navigate]);

    const handleMockLogin = () => {
        localStorage.setItem('token', 'mock-token-123');
        navigate('/');
    };

    return (
        <div className="min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900">
            <div className="bg-white dark:bg-gray-800 p-8 rounded-xl shadow-2xl w-full max-w-sm text-center">
                <h2 className="text-3xl font-bold mb-6 text-indigo-600 dark:text-indigo-400">Todo App Login</h2>
                <p className="text-gray-600 dark:dark:text-gray-400 mb-6">
                    Authentication logic is skipped in this demo. Click below to proceed.
                </p>
                <button
                    onClick={handleMockLogin}
                    className="w-full py-3 bg-indigo-600 text-white font-semibold rounded-lg hover:bg-indigo-700 transition duration-150 shadow-lg"
                >
                    Log In (Mock Session)
                </button>
            </div>
        </div>
    );
}

function Dashboard({ theme, toggleTheme }) {
    const navigate = useNavigate();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        const mockFetch = new Promise((resolve) => {
            setTimeout(() => {
                resolve({
                    id: 'mock-user-123',
                    email: 'test@example.com',
                    firstname: 'Alex',
                    name: 'Johnson'
                });
            }, 500);
        });

        mockFetch
            .then(data => {
                if (data && data.id) {
                    setUser(data);
                } else {
                    localStorage.removeItem('token');
                    navigate('/login');
                }
            })
            .catch(() => {
                localStorage.removeItem('token');
                navigate('/login');
            });
    }, [navigate]);

    const displayUserName = useMemo(() => {
        return user
            ? `${user.firstname || ''} ${user.name || ''}`.trim()
            : 'User';
    }, [user]);

    return (
        <>
            <nav className="flex justify-between items-center p-4 bg-white dark:bg-gray-800 shadow-md">
                <h1 className="text-3xl font-bold text-indigo-600 dark:text-indigo-400">
                    Welcome, {displayUserName || 'User'}!
                </h1>
                <div className="flex space-x-3">
                    <button
                        onClick={toggleTheme}
                        className="p-2 rounded-full bg-gray-200 dark:bg-gray-700 hover:ring-2 ring-indigo-500 transition-all text-xl"
                    >
                        {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
                    </button>
                    <button
                        onClick={() => {
                            localStorage.removeItem('token');
                            navigate('/login');
                        }}
                        className="px-4 py-2 bg-red-600 text-white font-medium rounded-lg hover:bg-red-700 transition duration-150 shadow-md"
                    >
                        Logout
                    </button>
                </div>
            </nav>
            <main className="container mx-auto p-4">
                {user ? (
                    <TodoList userId={user.id} />
                ) : (
                    <p className="text-center text-lg mt-10 text-gray-700 dark:text-gray-300">Loading your profile and tasks...</p>
                )}
            </main>
        </>
    );
}

const initialTheme = localStorage.getItem('theme') || 'light';

export default function App() {
    const [theme, setTheme] = useState(initialTheme);

    const toggleTheme = useCallback(() => {
        setTheme(prevTheme => {
            const newTheme = prevTheme === 'light' ? 'dark' : 'light';
            localStorage.setItem('theme', newTheme);
            return newTheme;
        });
    }, []);

    useEffect(() => {
        document.body.className = theme === 'dark' ? 'dark' : 'light';
    }, [theme]);

    return (
        <BrowserRouter>
            <div className={`min-h-screen font-sans ${theme === 'dark' ? 'bg-gray-900 text-white' : 'bg-gray-100 text-gray-900'}`}>
                <Routes>
                    <Route path="/login" element={<Login />} />
                    <Route path="/" element={<Dashboard theme={theme} toggleTheme={toggleTheme} />} />
                    <Route path="*" element={<Login />} />
                </Routes>
            </div>
        </BrowserRouter>
    );
}
