import { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios'; // Import axios
import { useTheme } from '../context/ThemeContext';
import TodoList from './TodoList';
import '../styles/Dashboard.css';

function Dashboard() {
    const navigate = useNavigate();
    const { theme, toggleTheme } = useTheme();
    const [user, setUser] = useState(null);

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login');
            return;
        }

        const fetchProfile = async () => {
            try {
                // FIX: Use AXIOS and the correct port 8081
                const response = await axios.get('http://localhost:8080/user/profile', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                setUser(response.data);
            } catch (error) {
                console.error('Failed to fetch user profile:', error);
                // Redirect if the server rejects the token (401/403) or fails network check
                localStorage.removeItem('token');
                navigate('/login');
            }
        };

        fetchProfile();
    }, [navigate]);

    const displayUserName = user
        ? `${user.firstname || ''} ${user.name || ''}`.trim()
        : 'User';

    const getThemeIcon = () => {
        if (theme === 'light') return '‚òÄÔ∏è';
        if (theme === 'dark') return 'üåô';
        return 'üëì';
    };

    return (
        <div className={`dashboard ${theme}`}>
            <nav>
                <h1>Welcome, {displayUserName}!</h1>
                <div>
                    <button onClick={toggleTheme}>
                        {getThemeIcon()}
                    </button>
                    <button onClick={() => {
                        localStorage.removeItem('token');
                        navigate('/login');
                    }}>Logout</button>
                </div>
            </nav>
            {user ? (
                <TodoList userId={user.id} />
            ) : (
                <p>Loading your tasks...</p>
            )}
        </div>
    );
}

export default Dashboard;
