import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import '../styles/TodoList.css';

const STATUS_OPTIONS = ['not started', 'todo', 'in progress', 'done'];

const getOneWeekFromNow = () => {
    const date = new Date();
    date.setDate(date.getDate() + 7);
    const pad = (num) => num.toString().padStart(2, '0');
    return `${date.getFullYear()}-${pad(date.getMonth() + 1)}-${pad(date.getDate())} ${pad(date.getHours())}:${pad(date.getMinutes())}:${pad(date.getSeconds())}`;
};

const isOverdue = (dueTime) => {
    if (!dueTime) return false;
    return new Date(dueTime) < new Date();
};

const sortTodos = (todos) => todos.slice().sort((a, b) => new Date(a.due_time) - new Date(b.due_time));

const formatTime = (timestamp, prefix) => {
    if (!timestamp) return '';
    const date = new Date(timestamp);
    return `${prefix}: ${date.toLocaleDateString()} ${date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}`;
};

const formatDueTimeForInput = (dueTime) => {
    if (!dueTime) return '';
    const [datePart, timePart] = dueTime.split(' ');
    return `${datePart}T${timePart.substring(0, 5)}`;
};

const TodoCard = React.memo(({ todo, deleteTodo, updateStatus, openEditModal }) => {
    const isDone = todo.status === 'done';
    const isTaskOverdue = isOverdue(todo.due_time) && !isDone;
    const statusClass = {
        done: 'done-task',
        'in progress': 'in-progress-task',
        todo: 'todo-task'
    }[todo.status] || '';

    const handleDragStart = (e) => e.dataTransfer.setData('todoId', todo.id);

    return (
        <div className={`todo-item ${isTaskOverdue ? 'overdue-task' : ''} ${statusClass}`} draggable onDragStart={handleDragStart}>
            <div className="card-content">
                <h3 className={isDone ? 'line-through done-task-text' : ''}>{todo.title}</h3>
                <p className={isDone ? 'line-through done-task-text' : ''}>{todo.description}</p>
                {isTaskOverdue && <p className="overdue-text-flag">OVERDUE</p>}
                <small className="card-small-text">{formatTime(todo.due_time, 'Due')}</small>
                <br />
                <small className="created-at-text card-small-text">{formatTime(todo.created_at, 'Created')}</small>
            </div>
            <div className="card-actions">
                <button onClick={() => openEditModal(todo)} className="edit-button">Edit</button>
                <button onClick={() => deleteTodo(todo.id)} className="delete-button">Remove</button>
            </div>
        </div>
    );
});

const KanbanColumn = ({ status, todos, updateStatus, deleteTodo, openEditModal }) => {
    const handleDrop = (e) => {
        e.preventDefault();
        const todoId = e.dataTransfer.getData('todoId');
        if (todoId) updateStatus(parseInt(todoId, 10), status);
    };
    const handleDragOver = (e) => e.preventDefault();
    const displayHeader = {
        'not started': 'Ideas',
        todo: 'To Do',
        'in progress': 'In Progress',
        done: 'Done'
    }[status] || status;

    return (
        <div className={`kanban-column ${status.replace(/\s/g, '-')}`} onDrop={handleDrop} onDragOver={handleDragOver}>
            <h2>{displayHeader} ({todos.length})</h2>
            <div className="column-tasks">
                {todos.map(todo => (
                    <TodoCard key={todo.id} todo={todo} updateStatus={updateStatus} deleteTodo={deleteTodo} openEditModal={openEditModal} />
                ))}
            </div>
        </div>
    );
};

const EditTodoModal = ({ todo, onClose, onSave }) => {
    const [title, setTitle] = useState(todo.title);
    const [description, setDescription] = useState(todo.description || '');
    const [dueDate, setDueDate] = useState(formatDueTimeForInput(todo.due_time));
    const [status, setStatus] = useState(todo.status);
    const [error, setError] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!title) return setError("Title cannot be empty.");
        await onSave({ ...todo, title, description, due_time: dueDate ? dueDate.replace('T', ' ') + ':00' : getOneWeekFromNow(), status });
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={e => e.stopPropagation()}>
                <h2>Edit Task</h2>
                <form onSubmit={handleSubmit}>
                    <label>Title</label>
                    <input type="text" value={title} onChange={e => setTitle(e.target.value)} required autoFocus />
                    <label>Description</label>
                    <textarea value={description} onChange={e => setDescription(e.target.value)} />
                    <label>Due Date</label>
                    <input type="datetime-local" value={dueDate} onChange={e => setDueDate(e.target.value)} />
                    <label>Status</label>
                    <select value={status} onChange={e => setStatus(e.target.value)}>
                        {STATUS_OPTIONS.map(s => <option key={s} value={s}>{s.charAt(0).toUpperCase() + s.slice(1)}</option>)}
                    </select>
                    {error && <p className="modal-error">{error}</p>}
                    <div className="modal-actions">
                        <button type="button" onClick={onClose} className="cancel-button">Cancel</button>
                        <button type="submit" className="save-button">Save Changes</button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default function TodoList({ userId }) {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({ title: '', description: '', dueDate: '' });
    const [editingTodo, setEditingTodo] = useState(null);
    const [searchQuery, setSearchQuery] = useState('');
    const [error, setError] = useState('');

    useEffect(() => {
        const fetchTodos = async () => {
            const token = localStorage.getItem('token');
            if (!token) return;
            try {
                const res = await axios.get('http://localhost:8080/todos', { headers: { Authorization: `Bearer ${token}` } });
                setTodos(sortTodos(res.data));
            } catch (err) { console.error(err); }
        };
        fetchTodos();
    }, []);

    const addTodo = async (e) => {
        e.preventDefault();
        setError('');
        if (!newTodo.title) return setError("Title cannot be empty.");
        const token = localStorage.getItem('token');
        const payload = { title: newTodo.title, description: newTodo.description, userId, status: 'not started', due_time: newTodo.dueDate ? newTodo.dueDate.replace('T', ' ') + ':00' : getOneWeekFromNow() };
        try {
            const res = await axios.post('http://localhost:8080/todos', payload, { headers: { Authorization: `Bearer ${token}` } });
            if (res.data?.id) setTodos(sortTodos([...todos, res.data]));
            setNewTodo({ title: '', description: '', dueDate: '' });
        } catch (err) { setError(err.message); }
    };

    const updateStatus = async (id, newStatus) => {
        setTodos(todos.map(todo => (todo.id === id ? { ...todo, status: newStatus } : todo)));
        try {
            const token = localStorage.getItem('token');
            await axios.put(`http://localhost:8080/todos/${id}`, { status: newStatus }, { headers: { Authorization: `Bearer ${token}` } });
        } catch (err) { setError(err.message); }
    };

    const deleteTodo = async (id) => {
        if (!window.confirm("Are you sure you want to delete this task?")) return;
        setTodos(todos.filter(todo => todo.id !== id));
        try { const token = localStorage.getItem('token'); await axios.delete(`http://localhost:8080/todos/${id}`, { headers: { Authorization: `Bearer ${token}` } }); } catch (err) { setError(err.message); }
    };

    const handleSaveEdit = async (updatedTodo) => {
        setTodos(sortTodos(todos.map(t => (t.id === updatedTodo.id ? updatedTodo : t))));
        try {
            const token = localStorage.getItem('token');
            await axios.put(`http://localhost:8080/todos/${updatedTodo.id}`, updatedTodo, { headers: { Authorization: `Bearer ${token}` } });
        } catch (err) { setError(err.message); }
        setEditingTodo(null);
    };

    const filteredTodos = todos.filter(todo =>
        todo.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
        (todo.description && todo.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    const groupedTodos = filteredTodos.reduce((acc, todo) => {
        const status = todo.status || 'not started';
        if (!acc[status]) acc[status] = [];
        acc[status].push(todo);
        return acc;
    }, {});

    return (
        <div className="kanban-board-container">
            {error && <p className="global-error-message">{error}</p>}
            <div className="controls-and-form">
                <input type="text" placeholder="Search tasks..." value={searchQuery} onChange={e => setSearchQuery(e.target.value)} />
                <form onSubmit={addTodo}>
                    <input type="text" placeholder="Title" value={newTodo.title} onChange={e => setNewTodo({ ...newTodo, title: e.target.value })} required />
                    <textarea placeholder="Description" value={newTodo.description} onChange={e => setNewTodo({ ...newTodo, description: e.target.value })} />
                    <input type="datetime-local" value={newTodo.dueDate} onChange={e => setNewTodo({ ...newTodo, dueDate: e.target.value })} />
                    <button type="submit">Add Task</button>
                </form>
            </div>

            <div className="kanban-board">
                {STATUS_OPTIONS.map(status => (
                    <KanbanColumn key={status} status={status} todos={groupedTodos[status] || []} updateStatus={updateStatus} deleteTodo={deleteTodo} openEditModal={setEditingTodo} />
                ))}
            </div>

            {editingTodo && <EditTodoModal todo={editingTodo} onClose={() => setEditingTodo(null)} onSave={handleSaveEdit} />}
        </div>
    );
}
