// In TodoList.jsx

function TodoList() {
    const [todos, setTodos] = useState([]);
    const [newTodo, setNewTodo] = useState({ title: '', description: '' });
    const [error, setError] = useState(null); // Add state for error messages
    const [isLoading, setIsLoading] = useState(false); // Add state for loading

    // ... (useEffect for fetching)

    const addTodo = async (e) => {
        e.preventDefault();
        setError(null); // Clear previous errors
        setIsLoading(true); // Start loading

        const token = localStorage.getItem('token');
        if (!token) {
            setError("Authentication token not found. Please log in.");
            setIsLoading(false);
            return;
        }

        try {
            const res = await axios.post('http://localhost:8080/todos', newTodo, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setTodos([...todos, res.data]);
            setNewTodo({ title: '', description: '' });
        } catch (err) {
            // Log the error and display a user-friendly message
            console.error("Error adding todo:", err.response || err);
            setError("Failed to add todo. Server responded with an error.");
        } finally {
            setIsLoading(false); // Stop loading regardless of success/failure
        }
    };

    return (
        <div className="todo-list">
            {/* Display error message */}
            {error && <p style={{ color: 'red' }}>{error}</p>}

            <form onSubmit={addTodo}>
                {/* ... (inputs and textarea) ... */}
                <button type="submit" disabled={isLoading}>
                    {isLoading ? 'Adding...' : 'Add Todo'}
                </button>
            </form>
            {/* ... (todos list) ... */}
        </div>
    );
}
